use std::fs::File;
use std::io::{Read, Write};
use std::path::Path;

/// Replaces consecutive duplicate words in each line of a file with a given replacement word.
/// 
/// # Arguments
/// * `file_path` - Path to the text file to process
/// * `replacement_word` - Word to replace consecutive duplicates with
/// 
/// # Returns
/// * `Result<(), std::io::Error>` - Returns Ok(()) on success, or an IO error if file operations fail
fn replace_duplicate_words(file_path: &str, replacement_word: &str) -> Result<(), std::io::Error> {
    // Read the entire file content
    let mut file = File::open(file_path)?;
    let mut contents = String::new();
    file.read_to_string(&mut contents)?;

    // Process each line
    let modified_lines: Vec<String> = contents.lines()
        .map(|line| {
            let words: Vec<&str> = line.split_whitespace().collect();
            if words.is_empty() {
                return line.to_string();
            }

            let mut new_words = Vec::new();
            let mut i = 0;
            while i < words.len() {
                if i < words.len() - 1 && words[i] == words[i + 1] {
                    new_words.push(replacement_word);
                    while i < words.len() - 1 && words[i] == words[i + 1] {
                        i += 1;
                    }
                } else {
                    new_words.push(words[i]);
                }
                i += 1;
            }
            new_words.join(" ")
        })
        .collect();

    // Write the modified content back to the file
    let mut file = File::create(file_path)?;
    for line in modified_lines {
        writeln!(file, "{}", line)?;
    }

    Ok(())
}

