fn test_replace_duplicate_words() {
    use std::fs::File;
    use std::io::{Read, Write};
    use std::path::Path;

    // Test case 1: Basic duplicates
    let path1 = "test1.txt";
    let content1 = "hello hello world\nthis is is a test\n";
    let mut file = File::create(path1).unwrap();
    writeln!(file, "{}", content1).unwrap();
    replace_duplicate_words(path1, "REPLACED").unwrap();
    let mut modified1 = String::new();
    File::open(path1).unwrap().read_to_string(&mut modified1).unwrap();
    assert_eq!(modified1, "REPLACED world\nthis REPLACED a test\n");
    std::fs::remove_file(path1).unwrap();

    // Test case 2: Multiple duplicates and single words
    let path2 = "test2.txt";
    let content2 = "single\nno duplicates here\nword word word\n";
    let mut file = File::create(path2).unwrap();
    writeln!(file, "{}", content2).unwrap();
    replace_duplicate_words(path2, "X").unwrap();
    let mut modified2 = String::new();
    File::open(path2).unwrap().read_to_string(&mut modified2).unwrap();
    assert_eq!(modified2, "single\nno duplicates here\nX\n");
    std::fs::remove_file(path2).unwrap();

    // Test case 3: Multiple spaces and duplicates
    let path3 = "test3.txt";
    let content3 = "a a b b c c\n  multiple   spaces  \n";
    let mut file = File::create(path3).unwrap();
    writeln!(file, "{}", content3).unwrap();
    replace_duplicate_words(path3, "DUPE").unwrap();
    let mut modified3 = String::new();
    File::open(path3).unwrap().read_to_string(&mut modified3).unwrap();
    assert_eq!(modified3, "DUPE DUPE DUPE\nmultiple spaces\n");
    std::fs::remove_file(path3).unwrap();
}

