fn max_subarray_sum_2d(matrix: &[Vec<i32>]) -> i32 {
    /// Computes the maximum subarray sum for a 1D array using Kadane's algorithm
    fn max_subarray_sum_1d(arr: &[i32]) -> i32 {
        let mut max_sum = arr[0];
        let mut current_sum = arr[0];

        for &num in &arr[1..] {
            current_sum = num.max(current_sum + num);
            max_sum = max_sum.max(current_sum);
        }

        max_sum
    }

    let mut max_sum = i32::MIN;

    // Check all row-wise subarrays
    for row in matrix {
        let row_max = max_subarray_sum_1d(row);
        max_sum = max_sum.max(row_max);
    }

    // Check all column-wise subarrays if matrix has at least one row
    if !matrix.is_empty() {
        for col in 0..matrix[0].len() {
            let col_array: Vec<i32> = matrix.iter().map(|row| row[col]).collect();
            let col_max = max_subarray_sum_1d(&col_array);
            max_sum = max_sum.max(col_max);
        }
    }

    max_sum
}

