#[test]
fn test_max_subarray_sum_2d() {
    assert_eq!(
        max_subarray_sum_2d(&[
            vec![1, 2, -1, -4, -20],
            vec![-8, -3, 4, 2, 1],
            vec![3, 8, 10, 1, 3],
            vec![-4, -1, 1, 7, -6],
        ]),
        29
    );
    assert_eq!(
        max_subarray_sum_2d(&[
            vec![-1, -2, -3],
            vec![-4, -5, -6],
        ]),
        -1
    );
    assert_eq!(
        max_subarray_sum_2d(&[
            vec![1, 2, 3],
            vec![4, 5, 6],
        ]),
        21
    );
    assert_eq!(
        max_subarray_sum_2d(&[
            vec![0, 0, 0],
            vec![0, 0, 0],
        ]),
        0
    );
    assert_eq!(
        max_subarray_sum_2d(&[
            vec![1, -2, 3],
            vec![-4, 5, -6],
            vec![7, -8, 9],
        ]),
        16
    );
}

