fn transform_character(c: char) -> char {
    // Transforms a single character according to the rules:
    // - Digits: increment by 1, wrapping around after 9
    // - Letters: increment by 1, wrapping around after 'z'
    // - Other characters remain unchanged
    if c.is_ascii_digit() {
        let digit = c.to_digit(10).unwrap();
        char::from_digit((digit + 1) % 10, 10).unwrap()
    } else if c.is_ascii_alphabetic() {
        if c == 'z' {
            'a'
        } else if c == 'Z' {
            'A'
        } else {
            ((c as u8) + 1) as char
        }
    } else {
        c
    }
}

fn transform_grid(grid: Vec<String>) -> Vec<String> {
    // Transforms each character in the grid according to transform_character rules
    grid.into_iter()
        .map(|row| row.chars().map(transform_character).collect())
        .collect()
}

