// Define a Product struct to represent product information
struct Product {
    name: String,
    price: f64,
    quantity: i32,
}

/// Calculates the total price of all products by multiplying each product's price by its quantity
/// and summing up all these values.
///
/// # Arguments
///
/// * `products` - A slice of Product structs containing name, price, and quantity
///
/// # Returns
///
/// The total price of all products as a floating-point number
fn calculate_total_price(products: &[Product]) -> f64 {
    let mut total_price = 0.0;
    for product in products {
        total_price += product.price * product.quantity as f64;
    }
    total_price
}

