    assert_eq!(calculate_total_price(&test_case2), 9.25);

    // Test case 3: Empty product list
    let test_case3: Vec<Product> = vec![];
    assert_eq!(calculate_total_price(&test_case3), 0.0);

    // Test case 4: Products with zero quantity
    let test_case4 = vec![
        Product {
            name: String::from("Expired Milk"),
            price: 2.99,
            quantity: 0,
        }
    ];
    assert_eq!(calculate_total_price(&test_case4), 0.0);
}

