use std::collections::{HashMap, HashSet, VecDeque};

/// Checks if it's possible to complete a circular flight tour visiting all cities.
/// A tour is possible if:
/// 1. Each city has equal in-degree and out-degree
/// 2. The graph is strongly connected (all cities are reachable from any starting city)
fn can_complete_tour(n: usize, flights: &[(usize, usize)]) -> &'static str {
    // If there are no flights, a tour is not possible
    if n == 0 {
        return "NO";
    }

    // Create adjacency list and track degrees
    let mut graph: HashMap<usize, Vec<usize>> = HashMap::new();
    let mut in_degree: HashMap<usize, usize> = HashMap::new();
    let mut out_degree: HashMap<usize, usize> = HashMap::new();
    let mut cities = HashSet::new();

    for &(src, dest) in flights {
        graph.entry(src).or_default().push(dest);
        *out_degree.entry(src).or_default() += 1;
        *in_degree.entry(dest).or_default() += 1;
        cities.insert(src);
        cities.insert(dest);
    }

    // Check if each city has equal in-degree and out-degree
    for &city in &cities {
        if in_degree.get(&city).unwrap_or(&0) != out_degree.get(&city).unwrap_or(&0) {
            return "NO";
        }
    }

    // Perform BFS to check connectivity
    fn bfs(start_city: usize, graph: &HashMap<usize, Vec<usize>>) -> HashSet<usize> {
        let mut visited = HashSet::new();
        let mut queue = VecDeque::new();
        queue.push_back(start_city);

        while let Some(city) = queue.pop_front() {
            if visited.insert(city) {
                if let Some(neighbors) = graph.get(&city) {
                    for &neighbor in neighbors {
                        if !visited.contains(&neighbor) {
                            queue.push_back(neighbor);
                        }
                    }
                }
            }
        }
        visited
    }

    // Pick any starting city
    let start_city = *cities.iter().next().unwrap();
    let visited_cities = bfs(start_city, &graph);

    if visited_cities.len() == cities.len() {
        "YES"
    } else {
        "NO"
    }
}
