        "YES"  // Complete cycle
    );
    assert_eq!(
        can_complete_tour(4, &[(0, 1), (1, 2), (2, 3), (3, 0)]),
        "YES"  // Complete cycle
    );
    assert_eq!(
        can_complete_tour(3, &[(0, 1), (1, 2), (2, 1)]),
        "NO"   // In-degree != out-degree for city 1
    );
    assert_eq!(
        can_complete_tour(4, &[(0, 1), (1, 2), (2, 0), (0, 3)]),
        "NO"   // City 3 is not strongly connected
    );
    assert_eq!(
