    ];
    assert_eq!(can_type_word(3, 3, &mut keyboard1, "abc"), true);

    let mut keyboard2 = vec![
        vec!['a', 'b', 'c'],
        vec!['d', 'e', 'f'],
        vec!['g', 'h', 'i'],
