fn top_k_average(scores: &[i32], k: usize) -> f64 {
    // Returns the average of the top K scores in the given list.
    // If there are fewer than K scores, returns the average of all the scores present.
    //
    // Args:
    // scores: Slice of integer scores.
    // k: The number of top scores to consider for the average.
    //
    // Returns:
    // The average of the top K scores as a floating-point number.

    if scores.is_empty() {
        return 0.0;
    }

    let k = k.min(scores.len()); // Ensure K does not exceed the length of the scores slice

    // Create a mutable vector to sort the scores in descending order
    let mut sorted_scores = scores.to_vec();
    sorted_scores.sort_by(|a, b| b.cmp(a)); // Sort in descending order

    // Take the top K scores
    let top_k_scores = &sorted_scores[..k];

    // Calculate the average of the top K scores
    let sum: i32 = top_k_scores.iter().sum();
    let average = sum as f64 / k as f64;

    average
}

