// Function to compute the maximum product of any two distinct elements in a list of integers.
// The function first checks if the list has at least two elements, then sorts the list.
// The maximum product can be either from the two largest positive numbers or from the two smallest negative numbers.
fn max_product_of_two(nums: &[i32]) -> Result<i32, String> {
    if nums.len() < 2 {
        return Err("At least two elements are required".to_string());
    }

    let mut sorted_nums = nums.to_vec();
    sorted_nums.sort();

    let product_first_two = sorted_nums[0] * sorted_nums[1];
    let product_last_two = sorted_nums[sorted_nums.len() - 1] * sorted_nums[sorted_nums.len() - 2];

    Ok(std::cmp::max(product_first_two, product_last_two))
}

assertions:
