    assert_eq!(max_product_of_two(&[1, 2, 3, 4]), Ok(12));
    assert_eq!(max_product_of_two(&[-10, -3, 5, 6]), Ok(30));
    assert_eq!(max_product_of_two(&[-5, -4, 0, 3, 7]), Ok(20));
    assert_eq!(
        max_product_of_two(&[1]),
        Err("At least two elements are required".to_string())
    );
}

