use std::collections::VecDeque;

// Solves a maze represented as a 2D grid of characters
// 'S' represents the start, 'E' represents the end
// '#' represents walls, '.' represents open paths
// Returns true if there's a path from 'S' to 'E', false otherwise
fn maze_solver(maze: &[Vec<char>]) -> bool {
    // Finds the coordinates of 'S' (start) and 'E' (end) in the maze
    fn find_start_end(maze: &[Vec<char>]) -> (Option<(usize, usize)>, Option<(usize, usize)>) {
        let mut start = None;
        let mut end = None;

        for (i, row) in maze.iter().enumerate() {
            for (j, &cell) in row.iter().enumerate() {
                if cell == 'S' {
                    start = Some((i, j));
                } else if cell == 'E' {
                    end = Some((i, j));
                }
            }
        }
        (start, end)
    }

    // Checks if a move to (x, y) is valid (within bounds, not a wall, not visited)
    fn is_valid_move(maze: &[Vec<char>], x: usize, y: usize, visited: &[Vec<bool>]) -> bool {
        let rows = maze.len();
        let cols = if rows > 0 { maze[0].len() } else { 0 };

        x < rows && y < cols && maze[x][y] != '#' && !visited[x][y]
    }

    // Depth-first search implementation to find path from start to end
    fn dfs(
        maze: &[Vec<char>],
        x: usize,
        y: usize,
        end: (usize, usize),
        visited: &mut [Vec<bool>],
    ) -> bool {
        if (x, y) == end {
            return true;
        }

        visited[x][y] = true;
        let directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]; // right, down, left, up

        for &(dx, dy) in &directions {
            let nx = x as isize + dx;
            let ny = y as isize + dy;

            if nx >= 0 && ny >= 0 {
                let nx = nx as usize;
                let ny = ny as usize;

                if is_valid_move(maze, nx, ny, visited) && dfs(maze, nx, ny, end, visited) {
                    return true;
                }
            }
        }
        false
    }

    let (start, end) = find_start_end(maze);
    if start.is_none() || end.is_none() {
        return false;
    }

    let start_pos = start.unwrap();
    let end_pos = end.unwrap();
    let rows = maze.len();
    let cols = if rows > 0 { maze[0].len() } else { 0 };

    let mut visited = vec![vec![false; cols]; rows];
    dfs(maze, start_pos.0, start_pos.1, end_pos, &mut visited)
}

