            vec!['S', '.', '.', '#'],
            vec!['#', '#', '.', '.'],
            vec!['#', '#', '.', 'E'],
        ]),
        true
    );

    // Simple unsolvable maze
    assert_eq!(
        maze_solver(&vec![
            vec!['S', '#', '.', '#'],
            vec!['#', '#', '.', '.'],
            vec!['#', '#', '.', 'E'],
        ]),
        false
    );

    // No start position
    assert_eq!(
        maze_solver(&vec![
            vec!['.', '.', '.', '#'],
            vec!['#', '#', '.', '.'],
            vec!['#', '#', '.', 'E'],
        ]),
        false
    );

    // No end position
    assert_eq!(
        maze_solver(&vec![
            vec!['S', '.', '.', '#'],
            vec!['#', '#', '.', '.'],
            vec!['#', '#', '.', '.'],
        ]),
        false
    );

    // Empty maze
    assert_eq!(
        maze_solver(&vec![]),
        false
    );
}

