use std::collections::HashMap;

pub type KeyType = i32;

#[derive(Default)]
struct CityDatabase {
    cities: HashMap<KeyType, Vec<String>>,
}

impl CityDatabase {
    // Optional constructor
    pub fn new() -> Self {
        Self {
            cities: HashMap::new(),
        }
    }

    // Add a city to the database
    pub fn add_city(&mut self, region_code: KeyType, city_name: String) -> Result<(), String> {
        if region_code <= 0 {
            return Err("Region code must be positive".to_string());
        }
        if city_name.trim().is_empty() {
            return Err("City name cannot be empty".to_string());
        }
        self.cities.entry(region_code).or_default().push(city_name);
        Ok(())
    }

    // Get all cities in a region
    pub fn get_cities_by_region(&self, region_code: KeyType) -> Vec<String> {
        self.cities
            .get(&region_code)
            .cloned()
            .unwrap_or_else(Vec::new)
    }

    // Get number of cities in a region
    pub fn count_cities_in_region(&self, region_code: KeyType) -> usize {
        self.cities
            .get(&region_code)
            .map(|v| v.len())
            .unwrap_or(0)
    }

    // Get all region codes
    pub fn get_all_region_codes(&self) -> Vec<KeyType> {
        self.cities.keys().cloned().collect()
    }

    // Find region code by city name (returns 0 if not found)
    pub fn find_region_by_city(&self, city_name: &str) -> KeyType {
        for (code, cities) in &self.cities {
            if cities.iter().any(|c| c == city_name) {
                return *code;
            }
        }
        0
    }

    // Remove all cities from a region
    pub fn remove_region(&mut self, region_code: KeyType) {
        self.cities.remove(&region_code);
    }

    // Print all cities grouped by region
    pub fn print_all_cities(&self) {
        const REGION_COL_WIDTH: usize = 12;

        println!("{:>width$} | {}", "Region Code", "City Name", width = REGION_COL_WIDTH);
        println!("------------|----------");

        let mut codes: Vec<_> = self.cities.keys().cloned().collect();
        codes.sort_unstable();

        for code in codes {
            if let Some(cities) = self.cities.get(&code) {
                for city in cities {
                    println!("{:>width$} | {}", code, city, width = REGION_COL_WIDTH);
                }
            }
        }
    }
}

