#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_basic_operations() {
        let mut db = CityDatabase::new();
        db.add_city(41, "Bytom".to_string()).unwrap();
        db.add_city(81, "Gdynia".to_string()).unwrap();
        db.add_city(81, "Sopot".to_string()).unwrap();

        let cities = db.get_cities_by_region(81);
        assert_eq!(cities.len(), 2);
        assert!(cities.contains(&"Gdynia".to_string()));
        assert!(cities.contains(&"Sopot".to_string()));
        assert_eq!(db.count_cities_in_region(41), 1);
    }

    #[test]
    fn test_finding_region_by_city() {
        let mut db = CityDatabase::new();
        db.add_city(27, "Starachowice".to_string()).unwrap();
        db.add_city(41, "Rybnik".to_string()).unwrap();
        db.add_city(27, "Sandomierz".to_string()).unwrap();

        assert_eq!(db.find_region_by_city("Rybnik"), 41);
        assert_eq!(db.find_region_by_city("Warsaw"), 0);
    }

    #[test]
    fn test_invalid_inputs() {
        let mut db = CityDatabase::new();
        assert!(db.add_city(0, "Invalid".to_string()).is_err());
        assert!(db.add_city(1, "".to_string()).is_err());
    }
}

