/**
 * Calculates the minimum number of operations required to make an array consecutive.
 * The operations consist of removing elements such that the remaining elements form a consecutive sequence.
 * The approach involves finding the maximum length of consecutive numbers in the array.
 * 
 * @param arr The input array of numbers.
 * @returns The minimum number of operations required (i.e., N - max_len).
 */
function calculateMinOperations(arr: number[]): number {
    const dp: Record<number, number> = {};
    let maxLen = 0;

    for (const x of arr) {
        const current = (dp[x - 1] || 0) + 1;
        dp[x] = current;
        if (current > maxLen) {
            maxLen = current;
        }
    }

    return arr.length - maxLen;
}

/**
 * Processes multiple test cases and calculates the results for each.
 * 
 * @param testCases An array of test cases, where each test case is an array of numbers.
 * @returns An array of results corresponding to each test case.
 */
function processTestCases(testCases: number[][]): number[] {
    const results: number[] = [];
    for (const testCase of testCases) {
        results.push(calculateMinOperations(testCase));
    }
    return results;
}

