/**
 * Calculates the smallest perfect square greater than each number in the input array.
 * A perfect square is an integer that is the square of another integer.
 * 
 * @param cases Array of numbers for which to find the smallest perfect square greater than each.
 * @returns Array of the smallest perfect squares greater than each input number.
 */
function smallestPerfectSquareGreaterThanCases(cases: number[]): number[] {
    return cases.map(n => {
        const nextSquareRoot = Math.ceil(Math.sqrt(n + 1));
        return nextSquareRoot * nextSquareRoot;
    });
}

