    );
    assert.deepStrictEqual(
        smallestPerfectSquareGreaterThanCases([0, 1, 2, 3]),
        [1, 4, 4, 4]
    );
    assert.deepStrictEqual(
        smallestPerfectSquareGreaterThanCases([24, 25, 26, 35]),
        [25, 36, 36, 36]
    );
    assert.deepStrictEqual(
        smallestPerfectSquareGreaterThanCases([99, 100, 101]),
        [100, 121, 121]
    );
}

testSmallestPerfectSquareGreaterThanCases();

