/**
 * Determines if each query string in Queries is a subsequence of string S.
 * 
 * @param S - The main string to check against
 * @param Q - Number of queries (unused in the function but kept for interface consistency)
 * @param Queries - Array of query strings to check
 * @returns Array of 'YES' or 'NO' for each query
 */
function isSubsequence(S: string, Q: number, Queries: string[]): string[] {
    const results: string[] = [];

    for (const query of Queries) {
        let sIndex = 0;
        let foundAll = true;

        for (const char of query) {
            // Find the character in S starting from current position
            const foundIndex = S.indexOf(char, sIndex);
            if (foundIndex === -1) {
                foundAll = false;
                break;
            }
            sIndex = foundIndex + 1;
        }

        results.push(foundAll ? "YES" : "NO");
    }

    return results;
}

