/**
 * Calculates the total sum of factorials of cycle lengths in a permutation.
 * The permutation is represented as an array of numbers where each element's value
 * indicates its position in the permutation.
 * 
 * @param n - The size of the permutation
 * @param a - The permutation array (0-based or 1-based depends on input)
 * @returns The total sum of factorials of cycle lengths (formatted to 9 decimal places)
 */
function calculateCycleFactorialSum(n: number, a: number[]): string {
    // Convert to 1-based positions array
    const pos: number[] = new Array(n + 1).fill(0);
    for (let idx = 0; idx < n; idx++) {
        const value = a[idx];
        pos[value] = idx + 1;  // Store as 1-based position
    }

    const visited: boolean[] = new Array(n + 1).fill(false);
    let total = 0.0;

    for (let i = 1; i <= n; i++) {
        if (!visited[i]) {
            let current = i;
            let cycleLength = 0;

            // Traverse the cycle
            while (!visited[current]) {
                visited[current] = true;
                cycleLength++;
                current = pos[current];
            }

            // Calculate factorial if cycle length is at least 2
            if (cycleLength >= 2) {
                let fact = 1;
                for (let k = 2; k <= cycleLength; k++) {
                    fact *= k;
                }
                total += fact;
            }
        }
    }

    // Format to 9 decimal places
    return total.toFixed(9);
}

