/**
 * Returns the maximum product of any two elements in the array nums.
 * If the array has fewer than 2 elements, returns 0.
 */
function maxProductOfTwo(nums: number[]): number {
    if (nums.length < 2) {
        return 0; // Not enough elements to form a pair
    }

    let firstMax = -Infinity;
    let secondMax = -Infinity;

    for (const num of nums) {
        if (num > firstMax) {
            secondMax = firstMax;
            firstMax = num;
        } else if (num > secondMax) {
            secondMax = num;
        }
    }

    return firstMax * secondMax;
}

assertions:
const assert = require('assert');

function testMaxProductOfTwo() {
    assert.strictEqual(maxProductOfTwo([1, 2, 3, 4]), 12);
    assert.strictEqual(maxProductOfTwo([5, 6, 7, 0]), 42);
    assert.strictEqual(maxProductOfTwo([0, 0, 0, 0]), 0);
    assert.strictEqual(maxProductOfTwo([-1, -2, -3, -4]), 2);
    assert.strictEqual(maxProductOfTwo([1, -2, 3, -4]), 3);
    assert.strictEqual(maxProductOfTwo([1]), 0);
    assert.strictEqual(maxProductOfTwo([]), 0);
}

testMaxProductOfTwo();

