/**
 * Finds the maximum interval length L where there's an interval of L non-chitters with all claims valid.
 * This is a simplified implementation that demonstrates the approach.
 */
function findMaxInterval(a: number[], b: number[]): [number, number] {
    // In a real implementation, we would use binary search and segment trees
    // to efficiently find the maximum valid interval length L.
    // For this example, we'll implement a basic approach that checks all possible intervals.

    let maxL = 0;
    let bestStart = 0;

    for (let i = 0; i < a.length; i++) {
        for (let j = i; j < a.length; j++) {
            const currentL = j - i + 1;
            let isValid = true;

            // Check if all claims in this interval are valid
            for (let k = i; k <= j; k++) {
                if (a[k] > currentL || b[k] > currentL) {
                    isValid = false;
                    break;
                }
            }

            if (isValid && currentL > maxL) {
                maxL = currentL;
                bestStart = i + 1; // converting to 1-based index
            }
        }
    }

    return [maxL, bestStart];
}

assertions:
