function testFindMaxInterval() {
    // Test case 1: Simple case where all claims are valid
    assert.deepStrictEqual(findMaxInterval([1, 2, 3], [1, 2, 3]), [3, 1]);

    // Test case 2: Case with one invalid claim
    assert.deepStrictEqual(findMaxInterval([5, 1, 1], [5, 1, 1]), [2, 2]);

    // Test case 3: All claims valid for maximum interval
    assert.deepStrictEqual(findMaxInterval([1, 1, 1, 1], [1, 1, 1, 1]), [4, 1]);

    // Test case 4: Mixed valid and invalid claims
    assert.deepStrictEqual(findMaxInterval([2, 3, 1, 4], [2, 3, 1, 4]), [4, 1]);
}

testFindMaxInterval();

