/**
 * Finds treasure cells in a grid and calculates total valid cells and digging time.
 * Treasure cells are defined as cells containing even digits (0, 2, 4, 6, 8).
 * @param n - Number of rows in the grid (unused in this implementation)
 * @param m - Number of columns in the grid (unused in this implementation)
 * @param grid - 2D array of strings representing the grid
 * @returns A tuple containing [totalValidCells, totalDiggingTime]
 */
function findTreasureCells(n: number, m: number, grid: string[][]): [number, number] {
    let totalValidCells = 0;
    let totalDiggingTime = 0;

    for (const row of grid) {
        for (const cell of row) {
            if ('02468'.includes(cell)) {
                totalValidCells++;
                totalDiggingTime += parseInt(cell, 10);
            }
        }
    }

    return [totalValidCells, totalDiggingTime];
}

