/**
 * Counts the number of subarrays that sum up to a given target.
 * Uses a hash map to store the frequency of cumulative sums encountered.
 * 
 * @param nums - An array of numbers.
 * @param target - The target sum to find.
 * @returns The number of subarrays that sum to the target.
 */
function countSubarraysWithSum(nums: number[], target: number): number {
    let count = 0;
    const sumCounts = new Map<number, number>();
    sumCounts.set(0, 1);
    let currentSum = 0;

    for (const num of nums) {
        currentSum += num;

        // If (currentSum - target) exists in the map, add its count to the result
        if (sumCounts.has(currentSum - target)) {
            count += sumCounts.get(currentSum - target)!;
        }

        // Update the map with the current sum
        if (sumCounts.has(currentSum)) {
            sumCounts.set(currentSum, sumCounts.get(currentSum)! + 1);
        } else {
            sumCounts.set(currentSum, 1);
        }
    }

    return count;
}

