/**
 * Calculates the total revenue generated from sales based on given prices and quantities.
 * 
 * @param prices - Array of numbers representing prices of various products.
 * @param quantities - Array of numbers representing the quantities sold for corresponding products.
 * @returns Total revenue generated as a number.
 */
function calculateRevenue(prices: number[], quantities: number[]): number {
    let totalRevenue = 0;
    for (let i = 0; i < prices.length; i++) {
        totalRevenue += prices[i] * quantities[i];
    }
