/**
 * Combines two arrays, sorts them, and finds the maximum difference between elements
 * where the difference is less than or equal to a given value k.
 * 
 * @param a First array of numbers
 * @param b Second array of numbers
 * @param k Maximum allowed difference between elements
 * @returns An object containing the sorted combined array and the maximum valid difference
 */
function processArrays(a: number[], b: number[], k: number): { combined: number[], maxDiff: number } {
    // Combine and sort the arrays
    const combined = [...a, ...b].sort((x, y) => x - y);

    let maxDiff = 0;
    let left = 0;
    const nTotal = combined.length;

    // Two pointers approach to find the maximum x - y <= k
    for (let right = 0; right < nTotal; right++) {
        while (combined[right] - combined[left] > k) {
            left++;
        }
        const currentDiff = combined[right] - combined[left];
        if (currentDiff > maxDiff) {
            maxDiff = currentDiff;
        }
    }

    return { combined, maxDiff };
}

