/**
 * Counts the number of special quadruples (i, j, k, l) where:
 * - i < j < k < l
 * - nums[i] * nums[j] == nums[k] * nums[l]
 */
function countSpecialQuadruples(nums: number[]): number {
    const n = nums.length;

    // preTwoProd[k] will store the count of products nums[i]*nums[j] for all i < j < k
    const preTwoProd: Map<number, number>[] = new Array(n);

    // Initialize the array with empty maps
    for (let i = 0; i < n; i++) {
        preTwoProd[i] = new Map<number, number>();
    }

    // Populate preTwoProd
    for (let k = 2; k < n; k++) {
        const j = k - 1;

        // Copy previous map (k-1) to current (k)
        preTwoProd[k - 1].forEach((value, key) => {
            preTwoProd[k].set(key, value);
        });

        // Add new products with current j
        for (let i = 0; i < j; i++) {
            const product = nums[i] * nums[j];
            const currentCount = preTwoProd[k].get(product) || 0;
            preTwoProd[k].set(product, currentCount + 1);
        }
    }

    let ans = 0;

    // Count valid quadruples
    for (let l = 0; l < n; l++) {
        const dVal = nums[l];

        for (let k = 0; k < l; k++) {
            const c = nums[k];

            if (c === 0) {
                if (dVal !== 0) {
                    continue;
                } else {
                    // Sum all counts in preTwoProd[k]
                    let sumCount = 0;
                    preTwoProd[k].forEach((count) => {
                        sumCount += count;
                    });
                    ans += sumCount;
                }
            } else {
                if (dVal % c !== 0) {
                    continue;
                }
                const q = dVal / c;
                ans += preTwoProd[k].get(q) || 0;
            }
        }
    }

    return ans;
}

