    assert.strictEqual(countSpecialQuadruples([2, 3, 4, 6, 8, 12]), 0);
    assert.strictEqual(countSpecialQuadruples([0, 0, 0, 0, 0]), 5);
    assert.strictEqual(countSpecialQuadruples([1, 2, 4, 8, 16, 32]), 4);
