/**
 * Simulates a circular selection process where elements are selected in a rotating manner.
 * @param K - Total number of elements
 * @param di - Array of indices to query (1-based)
 * @returns Array of values at the requested indices
 */
function simulateSelectionProcess(K: number, di: number[]): number[] {
    // Initialize available positions as a queue
    const available: number[] = Array.from({ length: K }, (_, i) => i);
    const ans: number[] = new Array(K).fill(0);

    for (let i = 1; i <= K; i++) {
        const currentLen = available.length;
        const steps = (i - 1) % currentLen;

        // Rotate the queue by moving 'steps' elements from front to back
        for (let j = 0; j < steps; j++) {
            available.push(available.shift()!);
        }

        // Select the first element in the rotated queue
        const pos = available.shift()!;
        ans[pos] = i;
    }

    // Return the values at the requested indices (1-based to 0-based conversion)
    return di.map(d => ans[d - 1]);
}

