/**
 * Normalizes numerical features in an array of objects to the range [0, 1].
 * Each object in the array represents a row with numerical features to be normalized.
 * Non-numerical features are left unchanged.
 * 
 * @param data Array of objects where each object represents a row with features.
 * @returns A new array of objects with numerical features normalized to [0, 1].
 */
function normalizeNumericalFeatures(data: Record<string, any>[]): Record<string, any>[] {
    if (data.length === 0) {
        return [];
    }

    // Create a deep copy of the original data to avoid mutation
    const normalizedData = JSON.parse(JSON.stringify(data));

    // Determine numerical columns by checking the first row (assuming consistent structure)
    const numericalColumns = Object.keys(data[0]).filter(key => {
        return typeof data[0][key] === 'number';
    });

    for (const column of numericalColumns) {
        // Extract all values for the current column
        const values = data.map(row => row[column]);
        const min = Math.min(...values);
        const max = Math.max(...values);

        if (max !== min) {
            // Normalize each value in the column
            for (const row of normalizedData) {
                row[column] = (row[column] - min) / (max - min);
            }
        } else {
            // Set to 0 if all values are the same (avoid division by zero)
            for (const row of normalizedData) {
                row[column] = 0;
            }
        }
    }

    return normalizedData;
}
const assert = require('assert');

function testNormalizeNumericalFeatures() {
    // Test case 1: Normal numerical features
    const testCase1 = [
        { age: 25, income: 50000, name: 'Alice' },
        { age: 30, income: 60000, name: 'Bob' },
        { age: 35, income: 70000, name: 'Charlie' }
    ];
    const result1 = normalizeNumericalFeatures(testCase1);
    assert.deepStrictEqual(result1, [
        { age: 0, income: 0, name: 'Alice' },
        { age: 0.5, income: 0.5, name: 'Bob' },
        { age: 1, income: 1, name: 'Charlie' }
    ]);

    // Test case 2: All values in a column are the same
    const testCase2 = [
        { age: 40, income: 80000, name: 'Dave' },
        { age: 40, income: 80000, name: 'Eve' },
        { age: 40, income: 80000, name: 'Frank' }
