    assert.deepStrictEqual(result2, [
        { age: 0, income: 0, name: 'Dave' },
        { age: 0, income: 0, name: 'Eve' },
        { age: 0, income: 0, name: 'Frank' }
    ]);

    // Test case 3: Mixed numerical and non-numerical features
    const testCase3 = [
        { age: 20, income: 30000, name: 'Grace', isStudent: true },
