/**
 * Calculates the total time required to complete all orders when the kitchen schedules
 * orders optimally to minimize total wait time.
 * 
 * @param n - Number of orders
 * @param k - Maximum kitchen capacity (number of orders that can be worked on simultaneously)
 * @param preparationTimes - Array of numbers representing preparation time for each order
 * @returns Total time required to complete all orders
 */
function totalTimeToCompleteOrders(n: number, k: number, preparationTimes: number[]): number {
    if (n <= k) {
        return Math.max(...preparationTimes);
    }

    // Sort preparation times in ascending order
    preparationTimes.sort((a, b) => a - b);

    // Create a min-heap using an array (JavaScript doesn't have a built-in heap)
    const heap: number[] = [];

    // Helper functions to simulate min-heap behavior
    const heapPush = (value: number) => {
        heap.push(value);
        heap.sort((a, b) => a - b);
    };

    const heapPop = (): number => {
        return heap.shift()!;
    };

    // Initialize the heap with the first k orders
    for (let i = 0; i < k; i++) {
        heapPush(preparationTimes[i]);
    }

    // Process remaining orders
    for (let i = k; i < n; i++) {
        const earliestTime = heapPop();
        heapPush(earliestTime + preparationTimes[i]);
    }

    // The total time is the maximum value in the heap
    return Math.max(...heap);
}

