/**
 * Represents a graph as an adjacency list.
 */
type Graph = number[][];

/**
 * Calculates the maximum path sum in a tree using Depth-First Search (DFS).
 * The tree is represented as an undirected graph with nodes labeled from 0 to n-1.
 * 
 * @param n - The number of nodes in the tree.
 * @param nodeValues - An array where nodeValues[i] represents the value of node i.
 * @param edges - An array of tuples representing the edges between nodes.
 * @returns The maximum path sum in the tree.
 */
function maxPathSum(n: number, nodeValues: number[], edges: [number, number][]): number {
    // Build the graph as an adjacency list
    const graph: Graph = Array.from({ length: n }, () => []);
    for (const [u, v] of edges) {
        graph[u - 1].push(v - 1);
        graph[v - 1].push(u - 1);
    }

    let maxSum = -Infinity;

    /**
     * Performs a DFS traversal to calculate the maximum path sum.
     * 
     * @param node - The current node being visited.
     * @param parent - The parent node of the current node to avoid revisiting.
     * @returns The maximum path sum starting from the current node.
     */
    function dfs(node: number, parent: number): number {
        let currentMax = nodeValues[node];
        for (const neighbor of graph[node]) {
            if (neighbor === parent) {
                continue;
            }
            const maxFromChild = dfs(neighbor, node);
            maxSum = Math.max(maxSum, currentMax + maxFromChild);
            currentMax = Math.max(currentMax, nodeValues[node] + maxFromChild);
        }
        maxSum = Math.max(maxSum, currentMax);
        return currentMax;
    }

    dfs(0, -1);
    return maxSum;
}

