/**
 * Finds the maximum in each sliding window of size k in an array of numbers.
 * Uses a deque (double-ended queue) to efficiently track the maximum for each window.
 * @param nums - Array of numbers
 * @param k - Window size
 * @returns Array containing the maximum for each window
 */
function slidingWindowMax(nums: number[], k: number): number[] {
    const dq: number[] = []; // Acts as a deque (we'll use array with push/pop/shift)
    const result: number[] = [];

    for (let i = 0; i < nums.length; i++) {
        // Remove elements out of the current window from the front
        while (dq.length > 0 && dq[0] < i - k + 1) {
            dq.shift();
        }

        // Remove elements smaller than the current element from the back
        while (dq.length > 0 && nums[i] >= nums[dq[dq.length - 1]]) {
            dq.pop();
        }

        dq.push(i);

        // Once the window size reaches k, start collecting results
        if (i >= k - 1) {
            result.push(nums[dq[0]]);
        }
    }

    return result;
}

