/**
 * Determines if the provided integer n is a prime number.
 * @param n - The integer to check for primality.
 * @returns True if n is a prime number, false otherwise.
 */
function isPrime(n: number): boolean {
    if (n <= 1) {
        return false;
    }
    for (let i = 2; i <= Math.sqrt(n); i++) {
        if (n % i === 0) {
            return false;
        }
    }
    return true;
}

/**
 * Calculates the sum of the digits of a given positive integer n.
 * @param n - The positive integer whose digits are to be summed.
 * @returns A tuple containing the sum of the digits and a boolean indicating whether the sum is a prime number.
 */
function sumOfDigits(n: number): [number, boolean] {
    const digits = n.toString().split('');
    const total = digits.reduce((sum, digit) => sum + parseInt(digit, 10), 0);
    return [total, isPrime(total)];
}

