/**
 * Calculates the length of the Shortest Common Supersequence (SCS) for two strings.
 * SCS is the shortest string that contains both input strings as subsequences.
 * The approach uses Longest Common Subsequence (LCS) to compute SCS length.
 */
function calculateSCSLength(str1: string, str2: string): number {
    const P = str1.length;
    const Q = str2.length;

    // Initialize DP table for LCS with dimensions (P+1) x (Q+1)
    const dp: number[][] = Array.from({ length: P + 1 }, () => 
        Array.from({ length: Q + 1 }, () => 0)
    );

    // Fill the DP table
    for (let i = 1; i <= P; i++) {
        for (let j = 1; j <= Q; j++) {
            if (str1[i - 1] === str2[j - 1]) {
                dp[i][j] = dp[i - 1][j - 1] + 1;
            } else {
                dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
            }
        }
    }

    const lcsLength = dp[P][Q];
    return P + Q - lcsLength;
}
const assert = require('assert');

function testCalculateSCSLength() {
    assert.strictEqual(calculateSCSLength("abc", "ac"), 3);
    assert.strictEqual(calculateSCSLength("abc", "def"), 6);
    assert.strictEqual(calculateSCSLength("AGGTAB", "GXTXAYB"), 9);
    assert.strictEqual(calculateSCSLength("", ""), 0);
    assert.strictEqual(calculateSCSLength("a", "a"), 1);
    assert.strictEqual(calculateSCSLength("abc", "abc"), 3);
}

testCalculateSCSLength();

