/**
 * Calculates the maximum possible median of an array after performing up to `k` operations.
 * Each operation can increment an element by 1. The goal is to maximize the median.
 * 
 * @param n The number of elements in the array.
 * @param k The number of operations allowed.
 * @param a The array of numbers.
 * @returns The maximum possible median after performing the operations.
 */
function maximizeMedian(n: number, k: number, a: number[]): number {
    // Sort the array in ascending order
    a.sort((x, y) => x - y);

    const m = Math.floor(n / 2);
    let res = a[m];
    let sumNeeded = 0;
    let count = 1; // initially just the median itself

    // We need to consider from a[m] to a[n-1]
    for (let i = m + 1; i < n; i++) {
        const delta = a[i] - a[i - 1];
        if (k >= delta * count) {
            k -= delta * count;
            res += delta;
            count += 1;
        } else {
            res += Math.floor(k / count);
            k = 0;
            break;
        }
    }

    if (k > 0) {
        res += Math.floor(k / count);
    }

    return res;
}

