/**
 * Generates a list of numbers and substitutions based on divisibility rules.
 * For each number from 1 to n, checks divisibility against each rule (divisor, word).
 * If divisible, appends the corresponding word. If no rules apply, keeps the number.
 * @param n The upper limit of numbers to generate (inclusive).
 * @param rules An array of tuples where each tuple contains a divisor and a word to substitute.
 * @returns An array of numbers and/or strings based on the substitution rules.
 */
function advancedFizzBuzz(n: number, rules: [number, string][]): (number | string)[] {
    const result: (number | string)[] = [];
    for (let i = 1; i <= n; i++) {
        let substitution = "";
        for (const [divisor, word] of rules) {
            if (i % divisor === 0) {
                substitution += word;
            }
        }
        result.push(substitution || i);
    }
    return result;
}

