/**
 * Calculates the total distance based on the maximum positive and negative coordinates
 * along the x and y axes from a list of points.
 * 
 * @param points Array of [x, y] coordinates
 * @returns The total distance calculated as (x_pos + (-x_neg) + y_pos + (-y_neg)) * 2
 */
function calculateTotalDistance(points: [number, number][]): number {
    let xPos = 0;
    let xNeg = 0;
    let yPos = 0;
    let yNeg = 0;

    for (const [x, y] of points) {
        if (x !== 0 && y === 0) {
            if (x > xPos) {
                xPos = x;
            } else if (x < xNeg) {
                xNeg = x;
            }
        } else if (y !== 0 && x === 0) {
            if (y > yPos) {
                yPos = y;
            } else if (y < yNeg) {
                yNeg = y;
            }
        }
    }

    return (xPos + (-xNeg) + yPos + (-yNeg)) * 2;
}

