/**
 * Given a list of points in the 2D plane, 
 * returns the area of the smallest rectangle that can enclose all the points.
 * If the input array is empty, returns 0.
 */
function smallestRectangleArea(points: [number, number][]): number {
    if (points.length === 0) {
        return 0;
    }

    const xCoordinates = points.map(point => point[0]);
    const yCoordinates = points.map(point => point[1]);

    const minX = Math.min(...xCoordinates);
    const maxX = Math.max(...xCoordinates);
    const minY = Math.min(...yCoordinates);
    const maxY = Math.max(...yCoordinates);

    const width = maxX - minX;
    const height = maxY - minY;

    return width * height;
}

