/**
 * Calculates the minimum number of operations required to convert a word into a string where
 * no two adjacent characters have ASCII values differing by more than 1.
 * Each operation consists of changing a character to any other character.
 * 
 * @param word - The input word to process.
 * @returns The minimum number of operations required.
 */
function minOperations(word: string): number {
    const n = word.length;
    if (n === 0) {
        return 0;
    }

    // Convert each character to 0-25 for easier handling
    const chars: number[] = Array.from(word).map(c => c.charCodeAt(0) - 'a'.charCodeAt(0));

    // Initialize DP table
    const dp: number[][] = Array.from({ length: n }, () => Array(26).fill(Infinity));

    // Base case: first character
    for (let c = 0; c < 26; c++) {
        if (chars[0] === c) {
            dp[0][c] = 0;
        } else {
            dp[0][c] = 1;
        }
    }

    for (let i = 1; i < n; i++) {
        const current = chars[i];
        for (let c = 0; c < 26; c++) {
            let minPrev = Infinity;
            for (let prevC = 0; prevC < 26; prevC++) {
                if (Math.abs(c - prevC) > 1) {
                    if (dp[i-1][prevC] < minPrev) {
                        minPrev = dp[i-1][prevC];
                    }
                }
            }
            if (minPrev !== Infinity) {
                const cost = current === c ? 0 : 1;
                dp[i][c] = minPrev + cost;
            }
        }
    }

    // The answer is the minimum of the last row
    return Math.min(...dp[n-1]);
}

