    assert.strictEqual(minOperations("aaaaa"), 2);
    assert.strictEqual(minOperations("abddez"), 2);
    assert.strictEqual(minOperations("zyxyxyz"), 3);
    assert.strictEqual(minOperations(""), 0);
    assert.strictEqual(minOperations("a"), 0);
    assert.strictEqual(minOperations("ab"), 1);
}

testMinOperations();

