/**
 * Find all substrings of length k that appear in text at least 'threshold' times.
 * 
 * @param text - The input text as a string.
 * @param k - The length of the substrings to find.
 * @param threshold - The minimum number of occurrences required for a substring to be included in the result.
 * @returns An array of substrings that meet the criteria.
 */
function findSubstrings(text: string, k: number, threshold: number): string[] {
    if (k < 1 || k > text.length) {
        return [];
    }

    const substringCount: Record<string, number> = {};

    // Count occurrences of each substring of length k
    for (let i = 0; i <= text.length - k; i++) {
        const substring = text.substring(i, i + k);
        substringCount[substring] = (substringCount[substring] || 0) + 1;
    }

    // Filter substrings by the threshold
    const result: string[] = [];
    for (const substring in substringCount) {
        if (substringCount[substring] >= threshold) {
            result.push(substring);
        }
    }

    return result;
}

