/**
 * Finds the maximum GCD (Greatest Common Divisor) of any two numbers in an array.
 * The algorithm works by counting frequencies of numbers and checking divisors from the largest possible down to 1.
 * @param numbers Array of integers to analyze
 * @param maxA Maximum possible value in the array (for frequency array allocation)
 * @returns The maximum GCD found
 */
function findMaxGCD(numbers: number[], maxA: number = 5000000): number {
    // Initialize frequency array
    const freq: number[] = new Array(maxA + 1).fill(0);
    let currentMax = 0;

    // Count frequencies and find the maximum number in the array
    for (const num of numbers) {
        if (num > maxA) continue; // Skip numbers larger than our maximum
        freq[num]++;
        if (num > currentMax) currentMax = num;
    }

    // Check divisors from largest to smallest
    for (let d = currentMax; d >= 1; d--) {
