/**
 * Processes a list of items to find the maximum sum of B values where the sum of A values (multiplied by 2) is divisible by 10.
 * The function checks all combinations of 1, 2, or 3 items to find the maximum valid sum of B values.
 */
function findMaxSumB(items: [number, number][]): number {
    let maxSum = 0;

    // Check all combinations of 1 item
    for (const item of items) {
        const [a, b] = item;
        if (a % 10 === 0 && b > maxSum) {
            maxSum = b;
        }
    }

    // Check all combinations of 2 items
    for (let i = 0; i < items.length; i++) {
        for (let j = i + 1; j < items.length; j++) {
            const sumA = items[i][0] + items[j][0];
            const sumB = items[i][1] + items[j][1];
            if (sumA % 10 === 0 && sumB > maxSum) {
                maxSum = sumB;
            }
        }
    }

    // Check all combinations of 3 items
    for (let i = 0; i < items.length; i++) {
        for (let j = i + 1; j < items.length; j++) {
            for (let k = j + 1; k < items.length; k++) {
                const sumA = items[i][0] + items[j][0] + items[k][0];
                const sumB = items[i][1] + items[j][1] + items[k][1];
                if (sumA % 10 === 0 && sumB > maxSum) {
                    maxSum = sumB;
                }
            }
        }
    }

    return maxSum;
}

