/**
 * Calculates the minimum smoke produced when mixing colors according to specific rules.
 * The problem is solved using dynamic programming.
 * 
 * @param colors An array of color values to be mixed.
 * @returns The minimum smoke produced.
 */
function calculateMinSmoke(colors: number[]): number {
    const n = colors.length;
    const prefix: number[] = new Array(n + 1).fill(0);

    // Compute prefix sums to quickly calculate the sum of any subarray
    for (let i = 0; i < n; i++) {
        prefix[i + 1] = prefix[i] + colors[i];
    }

    // Initialize DP table
    const dp: number[][] = Array.from({ length: n }, () => new Array(n).fill(0));

    // Fill the DP table for all possible subarray lengths starting from 2
    for (let length = 2; length <= n; length++) {
        for (let i = 0; i <= n - length; i++) {
            const j = i + length - 1;
            dp[i][j] = Infinity;

            // Try all possible split points between i and j
            for (let k = i; k < j; k++) {
                const cost = dp[i][k] + dp[k + 1][j];
                const sumLeft = prefix[k + 1] - prefix[i];
                const sumRight = prefix[j + 1] - prefix[k + 1];
                const currentSmoke = (sumLeft % 100) * (sumRight % 100);
                const total = cost + currentSmoke;

                if (total < dp[i][j]) {
                    dp[i][j] = total;
                }
            }
        }
    }

    return dp[0][n - 1];
}

