/**
 * Returns the minimum number of operations needed to transform S into a subsequence of T.
 * This is calculated as the length of S minus the length of the longest common subsequence (LCS) between S and T.
 */
function minOperationsToSubsequence(S: string, T: string): number {
    /**
     * Calculates the length of the longest common subsequence between two strings.
     * Uses dynamic programming to build a 2D array where dp[i][j] represents the LCS length
     * for the first i characters of X and the first j characters of Y.
     */
    function longestCommonSubsequence(X: string, Y: string): number {
        const m = X.length;
        const n = Y.length;
        const dp: number[][] = Array.from({ length: m + 1 }, () => Array(n + 1).fill(0));

        for (let i = 1; i <= m; i++) {
            for (let j = 1; j <= n; j++) {
                if (X[i - 1] === Y[j - 1]) {
                    dp[i][j] = dp[i - 1][j - 1] + 1;
                } else {
                    dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
                }
            }
        }

        return dp[m][n];
    }

    const lcsLength = longestCommonSubsequence(S, T);
    return S.length - lcsLength;
}

